mapper.update:
UPDATE TBL_REPLY
SET REPLY_TEXT = #{replyText}
WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT
    REPLY_NO, REPLY_TEXT, REPLY_WRITER, REPLY_DATE, BOARD_NO
FROM TBL_REPLY
WHERE BOARD_NO = #{bn}
ORDER BY REPLY_NO
LIMIT #{p.pageStart}, #{p.amount}

SELECT COUNT(*)
FROM tbl_reply
where BOARD_NO = #{boardNo}
